import React, { Component } from 'react';
import './Agendamentos.scss';
import Rodape from '../Rodape';
import Simbolo from '../../assets/imagens/simbolo.png';
import Agendado from '../Agendado';
import Ra from '../Ra';
import { Link, Redirect } from 'react-router-dom';

export default class Horarios extends Component {

    constructor(props) {
        super(props);
        this.state = {
            dadosHorarios: []
            //horarios: []
        }
    }

    componentDidMount() {
        const apiUrl = 'http://localhost:5000/api/usuarioHorario';
        fetch(apiUrl)
            .then(res => res.json())
            .then((result) => {
                this.setState({ dadosHorarios: result })
                this.state.dadosHorarios.map((usuarioHorario) => {
                    if (usuarioHorario.raUsuario === document.getElementById("ra").innerHTML) {
                        this.setState({ dadosHorarios: result/*[...this.state.horarios, usuarioHorario.idHorario]*/ })
                    }
                })

                /*this.state.horarios.map((horario) => {
                    
                    document.createElement("div").innerHTML += `<div class='caixa'>< h1 > ${horario.materia}<br /><small>${horario.diaSemana} às ${horario.horario}</small><br /><br /><button class='desmarcar'>Desmarcar</button></div >`
                })*/
            },
                (error) => {
                }
            )
    }

    marcar = (hora) => {
        var idHorario = hora.idHorario;
        var raUsuario = document.getElementById("ra").innerHTML;
        var userForm = { raUsuario, idHorario }
        var apiUrl = 'http://localhost:5000/api/usuarioHorario'
        fetch(apiUrl, {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userForm)
        })
            .then(
                resp => {
                    if (resp.ok) {
                        //console.log(resp.json());
                        resp.json().then((data) => {
                            //console.log(data)
                        })
                    }
                    else {
                        console.log('Usuário inexistente ou servidor off-line.');
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })

        var materia = hora.materia;
        var isReservado = true;
        var horario = hora.horario
        var diaSemana = hora.diaSemana;
        var raMonitor = hora.raMonitor;
        userForm = { idHorario, materia, isReservado, horario, diaSemana, raMonitor }
        console.log(JSON.stringify(userForm));
        apiUrl = `http://localhost:5000/api/horario/${idHorario}`;
        fetch(apiUrl, {
            method: "PUT",
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Credentials": true,
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userForm)
        })
            .then(
                resp => {
                    if (resp.ok) {
                        //console.log(resp.json());
                        resp.json().then((data) => {
                            //console.log(data);
                            //console.log(data)
                        })
                    }
                    else {
                        console.log('Usuário inexistente ou servidor off-line.');
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })

        document.getElementById(hora.idHorario + "btn").remove();
        document.getElementById(hora.idHorario).classList.add("reservado");
        alert(`Horário reservado com sucesso! ${hora.diaSemana} às ${hora.horario.substring(11, 16)}, matéria: ${hora.materia}`);
    }

    fetcho() {

    }

    render() {
        const { dadosHorarios } = this.state
        return (
            <div>
                <div className="invis"><Ra></Ra></div>
                <div className="menu">
                    <span><Link to="/">Início</Link></span>
                    <span><Link to="/agendamentos">Meus agendamentos</Link></span>
                    <span><Link to="/logoff">Logoff</Link></span>
                </div>
                <img src={Simbolo} alt="Monitorário" id="img" />
                <div className="aux"><Rodape /></div>
                <div className="emCima"></div>
                <div className="aux2"></div>
                <div className="content has-shown">
                    <h1 className="titulo"><b>Meus agendamentos</b></h1>

                    
                    {
                        dadosHorarios.map(
                            (horario) => {
                                fetch("http://localhost:5000/api/horario/" + horario.idHorario)
                                    .then(res => res.json())
                                    .then((result) => {
                                        //<Agendado materia={result.materia} diaSemana={result.diaSemana} horario={result.horario} />
                                        return (<div className="caixa">
                                            <h1>{result.materia}</h1>
                                            <br />
                                            <small>{result.diaSemana} às {result.horario}</small>
                                            <br /><br />
                                            <button className="desmarcar">Desmarcar</button>
                                        </div>)
                                    })


                            }
                        )
                    }

                </div>
            </div>
        )
    }
}
