import React, { Component } from 'react';
import './Agendado.scss';

export default class Agendado extends Component {
    constructor(props) {
        super(props);
    }

    desmarcar = () => {
        const apiUrl = `http://localhost:5000/api/usuarioHorario/${this.props.idUsuarioHorario}`

        fetch(apiUrl, {
            method: "DELETE",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
        })
            .then(
                resp => {
                    if (!resp.ok) {
                        console.log('Usuário inexistente ou servidor off-line.');
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })



        apiUrl = `http://localhost:5000/api/horario/${this.props.idHorario}`;
        fetch(apiUrl, {
            method: "PUT",
            headers: {
                "Access-Control-Allow-Origin": "*",
