import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';

import './Login.scss';

const Login = () => {
    const [email, setEmail] = useState("");
    const [senha, setPassword] = useState("");
    const [user, setUser] = useState();
    const [erro, setErro] = useState('');

    function mostrarSenha() {
        var x = document.getElementById("senha");

        if (x.type === "password") {
            x.type = "text";
        } else {
            x.type = "password";
        }
    }

    const handleSubmit = async e => {
        e.preventDefault();

        const userForm = { email, senha };

        console.log(userForm);

        await fetch(`${fro}http://localhost:5000/api/usuario/${email.substring(2, 7)}`, {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userForm)
        })
            .then(
                resp => {
                    if (resp.ok) {
                        //console.log(resp.json());
                        resp.json().then((data) => {
                            console.log(data);
                            // set the state of the user
                            setUser(data);
                            // store the user in localStorage
                            localStorage.setItem('user', data);
                            //console.log(data)
                        })
                    }
                    else {
                        console.log('Usuário inexistente ou servidor off-line.');
                        setErro("Usuário inexistente ou servidor off-line.");
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })
    }

    if (user) {
        return <div>{user.user.email} is logged in</div>;
    }

    return (
        <div className="container has-shown">
            <form onSubmit={handleSubmit}>
                <h1>Login</h1>
                <div className="textbox">
                    <FontAwesomeIcon icon={faEnvelope} className="icon" />
                    <input type="email" value={email} onChange={({ target }) => setEmail(target.value)} name="" placeholder="E-mail" id="" pattern=".+@g.unicamp.br" />
                </div>
                <div className="textbox">
                    <FontAwesomeIcon icon={faLock} className="icon" />
                    <input type="password" value={senha} onChange={({ target }) => setPassword(target.value)} name="" placeholder="Senha" id="senha" />
                </div>
                <div className="mostrar">
                    <input type="checkbox" onClick={mostrarSenha} id="mostrar" /> <span className="txt">Mostrar Senha</span>
                    <span className="checkmark"></span>
                </div>
                <p>
                    Não tem conta?
                    <Link to="/signup"> <b>Cadastre-se!</b></Link>
                </p>
                <input className="btn" type="submit" value="Entrar" />
            </form>
        </div>
    );
};

export default Login;
