import { createStore } from 'redux';

const INITIAL_STATE = {
    raUsuario: ''
}

function reducer(state = INITIAL_STATE, action) {
    if (action.type === 'LOGIN') {
        return { raUsuario: action.raUsuario }
    }

    return state;
}

const store = createStore(reducer);

export default store;
