import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';

import './Login.scss';

const Login = () => {
    const [email, setEmail] = useState("");
    const [senha, setPassword] = useState("");
    const [user, setUser] = useState();
    const [erro, setErro] = useState('');

    function mostrarSenha() {
        var x = document.getElementById("senha");

        if (x.type === "password") {
            x.type = "text";
        } else {
            x.type = "password";
        }
    }

    handleSubmit = async e => {

    }


    return (
        <div className="container has-shown">
            <form onSubmit={handleSubmit}>
                <h1>Login</h1>
                <div className="textbox">
                    <FontAwesomeIcon icon={faEnvelope} className="icon" />
                    <input type="email" value={this.email} onChange={({ target }) => this.setEmail(target.value)} name="" placeholder="E-mail" id="" pattern=".+@g.unicamp.br" />
                </div>
                <div className="textbox">
                    <FontAwesomeIcon icon={faLock} className="icon" />
                    <input type="password" value={this.senha} onChange={({ target }) => this.setPassword(target.value)} name="" placeholder="Senha" id="senha" />
                </div>
                <div className="mostrar">
                    <input type="checkbox" onClick={this.mostrarSenha} id="mostrar" /> <span className="txt">Mostrar Senha</span>
                    <span className="checkmark"></span>
                </div>
                <p>
                    NÃ£o tem conta?
                    <Link to="/signup"> <b>Cadastre-se!</b></Link>
                </p>
                <input className="btn" type="submit" value="Entrar" />
            </form>
        </div>
    );
};

export default Login;
