import React, { Component } from 'react';
import './Horarios.scss';

export default class Horarios extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dadosHorarios: [],
            dadosUsuarioHorarios: [],
            dadosUsuarios: []
        }
    }

    componentDidMount() {
        const apiUrl = 'http://localhost:5000/api/horario';

        fetch(apiUrl)
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    dadosHorarios: result
                });
                this.state.dadosHorarios.map((horario) => {
                    var obj = document.getElementById(horario.diaSemana.substring(0, 3).toLowerCase() + " " + horario.horario.substring(11, 16));
                    if (obj) {
                        obj.innerHTML += "<li>" + horario.materia + "</li>";

                        if (horario.isReservado) {
                            obj.classList.add("reservado");
                        } else {
                            obj.innerHTML += "<button id='" + obj.id+"btn" + "'>Marcar</button>";
                            document.getElementById(obj.id+"btn").addEventListener('click', () => this.marcar(horario), false)
                        }
                    }

                });
            },

                (error) => {
                    this.setState({ error });
                }
            )
    }

    marcar = (hora) => {
        var idHorario = hora.idHorario;
        var raUsuario = null;
        var userForm = { raUsuario, idHorario }

        console.log(raUsuario)
        var apiUrl = 'http://localhost:5000/api/usuarioHorario'
        fetch(apiUrl, {
            method: "POST",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userForm)
        })
            .then(
                resp => {
                    if (resp.ok) {
                        //console.log(resp.json());
                        resp.json().then((data) => {
                            console.log(data);
                            //console.log(data)
                        })
                    }
                    else {
                        console.log('Usuário inexistente ou servidor off-line.');
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })

        var materia = hora.materia;
        var isReservado = true;
        var horario = hora.horario
        var diaSemana = hora.diaSemana;
        var raMonitor = hora.raMonitor;
        userForm = { idHorario, materia, isReservado, horario, diaSemana, raMonitor }
        console.log(JSON.stringify(userForm));
        apiUrl = `http://localhost:5000/api/horario/${idHorario}`;
        fetch(apiUrl, {
            method: "PUT",
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Credentials": true,
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userForm)
        })
            .then(
                resp => {
                    if (resp.ok) {
                        //console.log(resp.json());
                        resp.json().then((data) => {
                            //console.log(data);
                            //console.log(data)
                        })
                    }
                    else {
                        console.log('Usuário inexistente ou servidor off-line.');
                    }
                })
            .catch(function (error) {
                console.log('There has been a problem with your fetch operation: ' + error.message);
            })

        document.getElementById(hora.diaSemana.substring(0, 3).toLowerCase() + " " + hora.horario.substring(11, 16) + "btn").remove();
        document.getElementById(hora.diaSemana.substring(0, 3).toLowerCase() + " " + hora.horario.substring(11, 16)).classList.add("reservado");
        alert(`Horário reservado com sucesso! ${hora.diaSemana} às ${hora.horario}, matéria: ${hora.materia}`);
    }

    render() {
        return (
            <div className="container">
                <div className="em cima"></div>
                <div></div>
                <h1 className="titulo">Horários</h1>
                <table className="listaHorarios">
                    <tbody>
                        <tr>
                            <td>Dia semana / Horario</td>
                            <td>Segunda</td>
                            <td>Terça</td>
                            <td>Quarta</td>
                            <td>Quinta</td>
                            <td>Sexta</td>
                        </tr>
                        <tr>
                            <td>7:30</td>
