                            </tr>
                            <tr className="linhaTabela">
                                <td className="horario">10:15</td>
                                <td id="seg 10:15"></td>
                                <td id="ter 10:15"></td>
                                <td id="qua 10:15"></td>
                                <td id="qui 10:15"></td>
                                <td id="sex 10:15"></td>
                            </tr>
                            <tr className="linhaTabela">
                                <td className="horario">11:05</td>
                                <td id="seg 11:05"></td>
                                <td id="ter 11:05"></td>
                                <td id="qua 11:05"></td>
                                <td id="qui 11:05"></td>
                                <td id="sex 11:05"></td>
                            </tr>
                            <tr className="linhaTabela">
                                <td className="horario">11:55</td>
                                <td id="seg 11:55"></td>
